            cout << \"Dapat sepeda\" << endl;
        } else {
            cout << \"Dapat buku\" << endl;
        }
    }"

"    int main() {
        int nomorTiket = 10;
        string status = \"panitia\";
        if (nomorTiket == 10) {
            if (status == \"panitia\") {
                cout << \"Hadiah batal\" << endl;
            } else {
                cout << \"Dapat sepeda\" << endl;
            }
        } else {
            cout << \"Dapat buku\" << endl;
        }
    }"

"    int main() {
        int nomorTiket = 10;
        string status = \"bukan panitia\";
        if (nomorTiket == 10 && status != "panitia") {
            cout << \"Dapat sepeda\";
        } else {
            cout << \"Hadiah batal\";
        }
    } // output : Dapat sepeda"

"    int main() {
        int nomorTiket = 10;
        string status = \"panitia\";
        if (nomorTiket == 10 || status != \"panitia\") {
            cout << \"Dapat sepeda\";
        } else {
            cout << \"Hadiah batal\";
        }
    } // output : Dapat sepeda"

"    int main() {
        int nomorTiket = 10;
        if (nomorTiket == 1) {
            cout << \"satu\";
        } else if (nomorTiket == 10) {
            cout << \"sepuluh\";
        } else if (nomorTiket == 20) {
            cout << \"dua puluh\";
        } else {
            cout << \"semua salah\";
        }
    } // output : sepuluh "

"    int main() {
        int nomorTiket = 11;
        switch (nomorTiket) {
            case 1:
                cout << \"satu\";
                break;
            case 11:
                cout << \"sebelas\";
                break;
            case 20:
                cout << \"dua puluh\";
                break;
            default:
                cout << \"semua salah\";
        }
    }"

"    int main() {
