"    int main() {
        int nomorTiket = 11;
        int position = 0;
        while (nomorTiket == 11) {
            cout << \"test\" << endl;
            if (position == 2) {
                nomorTiket = 5;
            }
            position++;
        }
    }
    /*
        test
        test
        test    */"

"    int main() {
        int usia;
        char repeat = \'n\';
        bool kondisi;
        do {
            cout << \"Masukan usia : \"; cin >> usia;
            if (usia <= 10) {
                cout << \"anak - anak\" << endl;
            } else if (usia > 10) {
                cout << \"remaja\" << endl;
            } else {
                cout << \"Input tidak sesuai\" << endl;
            }

            cout << \"Ingin mengulang program (y/n) ? \"; cin >> repeat;
            if (repeat == \'y\') {
                kondisi = true;
            } else {
                kondisi = false;
            }
        } while (kondisi == true);
    }"

"    for (inisialisasi; kondisi; increment/decrement) {
        // pernyataan
    }"

"    int main() {
        for (int a = 1; a <= 5; a++) {
            cout << a << endl;
        }
    }
    /*
        1
        2
        3
        4
        5    */"

"    int main() {
        for (double a = 5; a >= 1; a--) {
            cout << a << endl;
        }
    }
    /*
        5
        4
        3
        2
        1   */"

"    int main() {
